ons\nend\nend\n","undoManager":{"mark":-2,"position":0,"stack":[[{"start":{"row":58,"column":61},"end":{"row":59,"column":0},"action":"remove","lines":["",""],"id":2}]]},"ace":{"folds":[],"scrolltop":756.15625,"scrollleft":0,"selection":{"start":{"row":66,"column":3},"end":{"row":66,"column":3},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1534568329318}